"use strict";function add(){var e=parseInt(document.getElementById("x").value)+parseInt(document.getElementById("y").value);document.getElementById("result").innerHTML=e}function sub(){var e=parseInt(document.getElementById("x").value)-parseInt(document.getElementById("y").value);document.getElementById("result").innerHTML=e}document.getElementById("addButton").addEventListener("click",function(){add()}),document.getElementById("subButton").addEventListener("click",function(){sub()});